package Day14practice;

public class Pattern_03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int row=4;
char A='A';
for(int i=0;i<row;i++) {
	for(int j=0;j<row;j++) {
		System.out.print(A);
		A++;
	}System.out.println();
}
	}

}

/*

Square Pattern of Alphabetical Characters
ABCD
EFGH
IJKL
MNOP

*/